SELECT access_token, secret_token
FROM nessus_sessions
LIMIT 1;

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder

def flowFile = session.get()

if (!flowFile) return

def attrs = [:] as Map<String, String>

flowFile = session.write(flowFile, { rawIn, rawOut ->
    rawIn.withReader("UTF-8") { reader ->
        rawOut.withWriter("UTF-8") { writer ->

            def obj = new JsonSlurper().parse(reader)

            if (obj.containsKey("name"))
            {
                obj.name = obj.name.replace('\'', '') 
            }
            obj.each { k, v ->
                attrs[k] = v.toString()
            }

            new JsonBuilder(obj).writeTo(writer)
        }
    }
} as StreamCallback)

flowFile = session.putAllAttributes(flowFile, attrs)

session.transfer(flowFile, REL_SUCCESS)

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder

def flowFile = session.get()

if (!flowFile) return

def attrs = [:] as Map<String, String>

flowFile = session.write(flowFile, { rawIn, rawOut ->
    rawIn.withReader("UTF-8") { reader ->
        rawOut.withWriter("UTF-8") { writer ->

            def obj = new JsonSlurper().parse(reader)

            if (obj.containsKey("starttime"))
            {
                if (obj.starttime.toString() == 'null')
                    obj.starttime = '19000101T000000'
            }
            if (obj.containsKey("name"))
            {
                obj.name = obj.name.replace('\'', '') 
            }
            if (obj.containsKey("rrules"))
            {
                if (obj.rrules != null){
                    def splits = obj.rrules.split(';')
                    freq = splits[0].split('=') 
                    interval = splits[1].split('=') 
                    days = splits[2].split('=') 

                    obj.put('frequency',  freq[1])
                    obj.put('interval', interval[1])
                    obj.put('days', days[1])
                }
                else
                {
                    obj.put('frequency',  null)
                    obj.put('interval', null)
                    obj.put('days', null)
                }
            }
            obj.each { k, v ->
                attrs[k] = v.toString()
            }

            new JsonBuilder(obj).writeTo(writer)
        }
    }
} as StreamCallback)

flowFile = session.putAllAttributes(flowFile, attrs)

session.transfer(flowFile, REL_SUCCESS)

