import groovy.json.JsonSlurper
import groovy.json.JsonBuilder

def flowFile = session.get()

if (!flowFile) return

def attrs = [:] as Map<String, String>

flowFile = session.write(flowFile, { rawIn, rawOut ->
    rawIn.withReader("UTF-8") { reader ->
        rawOut.withWriter("UTF-8") { writer ->

            def obj = new JsonSlurper().parse(reader)

            if (obj.containsKey("description"))
            {
                obj.description = obj.description.replace('\'', '') 
                obj.description = obj.description.replace('\n', ' ') 
            }
            if (obj.containsKey("name"))
            {
                obj.name = obj.name.replace('\'', '') 

            }
            if (obj.containsKey("synopsis"))
            {
                obj.synopsis = obj.synopsis.replace('\'', '') 
            }
            if (!obj.containsKey("cvss"))
            {
                obj.put('cvss', 0)
            }


            obj.each { k, v ->
                attrs[k] = v.toString()
            }

            obj.put('file_id', flowFile.getAttribute('file_id'))
            obj.put('scan_id', flowFile.getAttribute('scan_id'))
            obj.put('history_id', flowFile.getAttribute('history_id'))

            new JsonBuilder(obj).writeTo(writer)
        }
    }
} as StreamCallback)

flowFile = session.putAllAttributes(flowFile, attrs)

session.transfer(flowFile, REL_SUCCESS)

INSERT INTO nessus_folders
    (name, folder_id)
VALUES
    (
     '${name}',
     ${id}
    )
ON CONFLICT (folder_id)
DO NOTHING;
